                        else{
                            $newLine = "<div class='footer_cities_link_wrapper'><a href='//" . Yii::$app->params['siteAddress'] . "'>В $city->name_dec</a></div>";
                        }
                        return $newLine;
                    }
                    
                    function createCityListFooter($subdomen_list){
                        $citiesListResult = "";
                        foreach ($subdomen_list as $key => $subdomen) {
                            $citiesListResult .= createCityNameLineFooter($subdomen);
                        }
                        echo $citiesListResult;
